const mongoose = require("mongoose");
const bcrypt = require("bcrypt");

const userSchema = new mongoose.Schema({
  name: { type: String },
  email: { type: String, required: true, unique: true },
  password: { type: String, required: true },
  age: { type: Number },
  gender: { type: String },
  location: { type: String },
  phone: { type: String },
  role: { type: String, default: "User" },
  image: {
    type: String,
    default:
      "https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/271deea8-e28c-41a3-aaf5-2913f5f48be6/de7834s-6515bd40-8b2c-4dc6-a843-5ac1a95a8b55.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..BopkDn1ptIwbmcKHdAOlYHyAOOACXW0Zfgbs0-6BY-E",
  },
});
/******************************************************************************************************* */
// This function for hashing the password when the user make a register before saving in the data-base //
userSchema.pre("save", async function () {
  this.email = this.email.toLowerCase();
  this.password = await bcrypt.hash(this.password, 10);
});
/************************************************************************************************* */
module.exports = mongoose.model("User", userSchema);
